using System;using System.Net;namespace Lattice.Discovery{    public sealed class Transmitter : Transport    {        public Transmitter(int port, Mode mode) : base(mode)        {            IPEndPoint remote = new IPEndPoint(IPAddress.Broadcast, port);            m_socket.Connect(remote);            Log.Debug($"Brodcasting to {m_socket.LocalEndPoint}");        }        public void Broadcast(Segment segment)        {            /*SendTo(segment, m_endPoint);*/
            m_socket.Send(segment.ToArray());        }    }}