using System;namespace Lattice{    public static class Log    {        // Debug Messages        public static Action<object> POut { get; set; }        public static Action<object> EOut { get; set; }        public static Action<object> WOut { get; set; }        // called everytime a packet is lost or retransmission of the same packet        public static Action Loss { get; set; }        internal static void Print(object value) => POut?.Invoke(value);        internal static void Error(object value) => EOut?.Invoke(value);        internal static void Warning(object value) => WOut?.Invoke(value);    }}